  // State hooks to store user input
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Helper function for password complexity
  const isPasswordStrong = (pwd) => {
    return pwd.length >= 8 && /\d/.test(pwd) && /[A-Z]/.test(pwd);
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent form refresh

    // Client-side validations
    if (!email || !password || !confirmPassword) {
      setError('All fields are required.');
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    if (!isPasswordStrong(password)) {
      setError('Password must be at least 8 characters, contain a number and uppercase letter.');
      return;
    }

    setError('');

    // Call backend to initiate signup
    const res = await fetch('/api/signup', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });

    const data = await res.json();
    if (res.ok) {
      setSuccess('Sign up successful! Please verify your email.');
    } else {
      setError(data.message || 'Signup failed.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Sign Up</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
